import React, {useState, useEffect} from 'react'
import APIService from '../APIService'
import Select from 'react-select'




function Form(props) {
    const [title, SetWorkouttitle] = useState('')
    const [all_exercises, SetAllExercises] = useState([])
    const [exercises, setExercises] = useState({exercise_name:"",reps:"",sets:""})

    const handleChange = e => {
        console.log(typeof(e))
        console.log("Drukujemy e ",e)
        const {name, value} = e.target;
        // console.log(name)
        setExercises(prevState => ({
            ...prevState,
            [name]:value
        }))
    }

    const UpdateWorkout = () => {
        APIService.UpdateWorkout(props.workout.id, {title, exercises})
        .then(resp => props.updatedInformation(resp))
    }

    const InsertWorkout = () => {
        APIService.CreateWorkout({title,exercises})
        .then(resp => props.insertedInformation(resp))
    }
    const insertExercise = (exercise) => {
        const new_exercises= [...exercises, exercise]
        setExercises(new_exercises)

    }


    useEffect(() => {
        SetWorkouttitle(props.workout.title)
        setExercises(props.workout.exercises)
    }, [props.workout])

    useEffect(() => {
        fetch('http://127.0.0.1:8000/exercise-list', {
        'method':'GET',
        headers: {
            'Content-Type':'application/json',
            }
            })
        .then(resp => resp.json())
        .then(resp => SetAllExercises(resp))
    }, [])

    let all_names = all_exercises.map(function(item){
        return item.exercise_name;
    })

    return (
        <div>

            {props.workout ? (
               <div className = "mb-3">
               <label htmlFor = "title" className = "form-label">{props.workout.name}</label>
               <input type="text" className = "form-control" id="title" placeholder = "Please enter workout name"
               value = {title} onChange = {e => SetWorkouttitle(e.target.value)}></input>

                <label htmlFor = "exercises" className = "form-label">Exercises</label>

                {/* <select className = "form-control" id="exercises" rows="5"
                value = {w_exercises.name} onChange = {e => SetWExercises(e.target.value)}
                ></select> */}
               <Select options={all_names} value={exercises.exercise_name} name="exercise_name" onChange= {(e) => {
               const {value, name} = e;
               console.log("------",value)
               console.log("--------",name)
               setExercises(prevState => ({
                ...prevState,
                [name]:value
                }))
               }
            } />

               <br/>
               <label htmlFor = "reps" className = "form-label">Reps</label>
               <br/>
               <input type="text" value={exercises.reps} onChange= {handleChange} name="reps" placeholder = "Number of reps"/>

               <br/>
               <label htmlFor = "sets" className = "form-label">Sets</label>
               <br/>
               <input type="text" value={exercises.sets} onChange= {handleChange} name="sets" placeholder = "Number of sets"/>

               <br/>
               {     props.workout.id ? 
                    <button onClick = {UpdateWorkout} className = "btn btn-success">Update Workout</button>:
                    <button onClick = {InsertWorkout} className = "btn btn-success">Create new workout</button>
                }

               </div>            
            ):null}
            {

            }

        </div>
    )
}

export default Form
