import React from 'react'
import {useState, useEffect} from 'react'
import ExerciseList from './components/ExerciseList'

import Form from './components/Form'
import Sidebar from './components/Sidebar'
import PlanList from './components/PlanList'
import {useCookies} from 'react-cookie';
import { Redirect, useHistory} from 'react-router-dom';
import {BrowserRouter, Switch, Route} from 'react-router-dom'
import Login from './components/Login'
import {CookiesProvider} from 'react-cookie'


function App() {

  let history = useHistory()

  const [plans, setPlans] = useState([])
  const [exercises, setExercises] = useState([])
  const [token, setToken, removeToken] = useCookies(['mytoken'])

  useEffect(() => {
    console.log("TESTOWANIE", token['mytoken'])

    fetch('http://127.0.0.1:8000/plans-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token['mytoken']}`,

      }
    })
    .then(resp => resp.json())
    .then(resp => setPlans(resp))


    fetch('http://127.0.0.1:8000/exercise-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',

      }
    })
    .then(resp => resp.json())
    .then(resp => setExercises(resp))

  }, [])



  const logoutBtn = () => {
    removeToken(['mytoken'])  
  }


  // const WorkoutForm = () => {
  //   setEditWorkout({title:'',exercises:[]})
  // }
  console.log(plans[0])

  return (
    <CookiesProvider>
    <BrowserRouter>
    <div className="App">

        <Switch>
          <Route exact path = "/">
            <Login/>
          </Route>
        </Switch>
        <Switch>
           <Route exact path ="/home">
              <div className = "col">
              {/* <button onClick = {WorkoutForm} className = "btn btn-primary">Insert Workout</button>
            </div>
            <WorkoutList workouts={workouts} editBtn={editBtn} deleteBtn={deleteBtn}/>
            {editWorkout ? <Form workout = {editWorkout} updatedInformation = {updatedInformation} insertedInformation = {insertedInformation}/> : null} 
              <div className = "col">
                 */}
                <PlanList plans={plans} />
                <button onClick = {logoutBtn} className = "btn btn-primary">Logout</button>
             </div>

              </Route>
          </Switch>


          <Switch>
            <Route exact path = "/exercises">
              <ExerciseList exercises={exercises}/> 
          </Route>
          </Switch>
          <Switch>
          <Route exact path = "/login">
            <Login/>
          </Route>
        </Switch>      

      </div>
      </BrowserRouter>
      </CookiesProvider>

  ) 
}


export default App;
