import React, { isValidElement } from 'react'
import {useForm} from "react-hook-form"
import APIService from '../APIService'
import Select from 'react-select'
import {useCookies} from 'react-cookie';
import {useState, useEffect} from 'react'

function GenerateForm(props) {

    const [planinfo, setPlaninfo] = useState({number:"", target:"", time:"", exercises:[]})
    const [error] = useState(false)
    const [token] = useCookies(['mytoken'])


    let all_names = props.exercises && props.exercises.map(function(item){
        return {value:item.exercise_name};
    })

    const isValid = (plan) => {
        return true
    }


    const handleSumbit = () => {
        if (isValid(planinfo))
            APIService.GeneratePlan(planinfo, token['mytoken'])
    }

    return (
        <form onSubmit={handleSumbit}>  
            <label className = "form-label">Number of workouts in plan</label>
            <Select
                name="number" 
                options = {[
                    {value:1},
                    {value:2},
                    {value:3},
                    {value:4}
                ]}
                getOptionLabel={options => options["value"]}
                getOptionValue={(options) => options['value']}
                required  
                type="number_of_workouts"
                onChange= {(e) => {
                    planinfo["number"] = e.value
                }}
            />
            <label className = "form-label">Plan Target</label>

            <Select name="target"
                options = {[{value:"Strength building"},{value:"Weight Loss"},{value:"Endurance"}]}
                getOptionLabel={options => options["value"]}
                getOptionValue={(options) => options['value']}
                required  
                type="target"
                onChange= {(e) => {
                    planinfo["target"] = e.value}} 
            />
            <label className = "form-label">Time per workout</label>
            <br/>
            <input name="time" placeholder = "Enter Time" required onChange= {(e) => {
                    planinfo["time"] = e.target.value
                    {(!Number(e.target.value) ||
                         0 > e.target.value ||
                          e.target.value > 120) ? setError(true) : error = false
                        }
                    }}/>
            <br/>

            <label className = "form-label">Mandatory exercises</label>
            <Select options={all_names} 
                isMulti
                className="basic-multi-select"
                getOptionLabel={options => options["value"]}
                getOptionValue={(options) => options['value']} 
                name="exercises"
                onChange= {(e) => {
                    planinfo["exercises"] = e}}
            />
             <input type="submit" value="WyÅ›lij" />
             {error && <h3>DUPA</h3>}
        </form>
    )
}

export default GenerateForm
