// import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      workouts:[],
      activeWorkout:{
        id:null,
        title:'',
      },
      editing:false,
    }
    this.fetchworkouts = this.fetchworkouts.bind(this)
  };
  componentDidMount(){
    this.fetchworkouts()
  }
  fetchworkouts(){
    console.log("Fetching...")
    fetch("http://127.0.0.1:8000/workout-list")
    .then(response => response.json())
    .then(data => 
        this.setState({
          workouts:data
        })
      ).catch(error => console.log(error))
  }


  render(){
    var workouts = this.state.workouts
    var self = this
    return (
      <div className="exercise-container">
        <div id="form-wrapper">
          <form id="form">
            <div className="flex-wrapper">
              <div style={{flex:6}}>
                <input className="form-control" id="title"></input>
              </div>
              <div style={{flex:1}}>
                <input id="submit" className="btn btn-warning" type="submit"></input>
              </div>
            </div>
          </form>
        </div>
        <div id="list-wrapper">
          {workouts.map(function(exercise,index){
            return(
              <div key={index} className="task-wrapper flex-wrapper">
                <div style={{flex:7}}>
                <span>{exercise.name}</span>
                </div>
                <div style={{flex:1}}>
                <button onClick={() => self.startEdit(exercise)} className="btn btn-sm btn-outline-info">Edit</button>
                </div>
                <div style={{flex:1}}>
                <button onClick={() => self.deleteItem(exercise)} className="btn btn-sm btn-outline-dark delete">-</button>
                </div>
                </div>
            )
          })}
        </div>
      </div>
    )
  }
}

export default App;
