import React from 'react'
import PropTypes from 'prop-types'
import APIService from '../APIService';
import {useState, useEffect} from 'react'
import { useParams } from "react-router-dom";
import {useCookies} from 'react-cookie';
import WorkoutList from './WorkoutList';
import ExerciseList from './ExerciseList';
import WorkoutForm from './WorkoutForm';
import Workout from './Workout';

function TrainingPlan(props) {



    let {slug} = useParams();
    const [token] = useCookies(['mytoken'])
    const [plan, setPlan] = useState("")
    const [workouts, SetWorkouts] = useState("")
    const [clicked, setClicked] = useState(false);


    const buttonHandler = () => {
        setClicked(current => !current)
    }


    useEffect(() => {
        console.log(slug)
        APIService.PlanDetail(slug,token['mytoken'])
        .then(resp => setPlan(resp))
        .then(plan && SetWorkouts(plan[0].workouts))
        .catch(error => console.log(error))
    }, [])


    const dltPlan = (plan) => {
        const new_plans = plans.filter(myplan => {
          if(myplan.id === plan.id) {
            return false
          }
          return true;
        })
        setPlans(new_plans)
      }

    const DeletePlan = (plan) => {
        APIService.PlanDelete(token['mytoken'],plan.id)
        .then(dltPlan(plan))
        .catch(error => console.log(error))
    }

    const addNewPlan = (plan) => {
        setPlans(plans => [...plans,plan])
    }



    return (
        <div>
            <br/>
            <button type="button" class="btn btn-success" style = {{height:"60px",width: "300px"}}onClick={buttonHandler} >
                 Create new Workout
            </button>

            <br/>
            <br/>

            {plan && plan[0].workouts.map(workout => {
                return (
                        <Workout workout={workout}/>
                )
            })}            
            {/* {plan && <WorkoutList workouts = {plan[0].workouts}/>} */}
            {clicked ? <WorkoutForm plan_id={plan[0].id} />:<div></div>}

        </div>
    )
}

