import React from 'react'
import PropTypes from 'prop-types'
import {useState, useEffect} from 'react'
import WorkoutForm from './WorkoutForm'
import { useParams } from 'react-router-dom';
import {useHistory} from 'react-router-dom';
import {useCookies} from 'react-cookie';
import PlanForm from './PlanForm';
import APIService from '../APIService';
function PlanList(props) {

    let history = useHistory()
    const [token] = useCookies(['mytoken'])
    const [clicked, setClicked] = useState(false)
    const [plans, setPlans] = useState([])
    const [chosenPlan,setChosenPlan] = useState("")
    const [showDoneForm, setShowDoneForm] = useState(false)

      const PlanDoneForm = (plan) => {
        setShowDoneForm(!showDoneForm);
        setChosenPlan(plan.id)
    }


    useEffect(() => {
        fetch('http://127.0.0.1:8000/plans-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token['mytoken']}`,

      }
    })
    .then(resp => resp.json())
    .then(resp => setPlans(resp))

    }, [])

    const buttonHandler = () => {
        setClicked(current => !current)
    }

    const dltPlan = (plan) => {
        const new_plans = plans.filter(myplan => {
          if(myplan.id === plan.id) {
            return false
          }
          return true;
        })
        setPlans(new_plans)
      }

    const DeletePlan = (plan) => {
        APIService.PlanDelete(token['mytoken'],plan.id)
        .then(dltPlan(plan))
        .catch(error => console.log(error))
    }

    const addNewPlan = (plan) => {
        setPlans(plans => [...plans,plan])
        setClicked(current => !current)
    }


    return (
        <div>  
            <br/>
            <br/>
            <div className="row">
        {plans && plans.map(plan => {           
            return(
                <div>

                <button type="button" className="PlanElement" onClick={() => history.push(`/plan/${plan.id}`)}>{plan.plan_name}
                    </ button>
                    <button className = "btn btn-danger " onClick={() => DeletePlan(plan)}> Delete Plan</button>
                    <button className = "btn btn-dark" onClick  = {() => PlanDoneForm(plan)}> Plan Instance Done</button>
                </div>
            )

        })}
        </div>

        {clicked ? <PlanForm addNew = {addNewPlan} /> :<div/> }
        <button type="button" class="btn btn-success middle" onClick={buttonHandler} >
            Create new Training Plan
        </button>

        </div>
    )
}

PlanList.propTypes = {

}

export default PlanList
