import React from 'react'
import {useState, useEffect} from 'react'
import ExerciseList from './components/ExerciseList'
import WorkoutList from './components/WorkoutList'
import Form from './components/Form'
import Sidebar from './components/Sidebar'
import {useCookies} from 'react-cookie';
import {useHistory} from 'react-router-dom';


function App() {

  const [workouts, setWorkouts] = useState([])
  const [editWorkout,setEditWorkout] = useState(null)
  const [exercises, setExercises] = useState([])
  const [token, setToken, removeToken] = useCookies(['mytoken'])

  useEffect(() => {

    fetch('http://127.0.0.1:8000/workout-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setWorkouts(resp))

    fetch('http://127.0.0.1:8000/exercise-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setExercises(resp))


  }, [])

  useEffect(() => {
    if(!token['mytoken']) {
        history.push('/')
        //window.location.href = '/'

    }
  }, [token])

  const editBtn = (workout) => {
    setEditWorkout(workout)

  }

  const updatedInformation = (workout) => {
    const new_workout = workouts.map(myworkout => {
      if(myworkout.id === workout.id) {
        return workout;
      }
      else {
        return myworkout;
      }
    })
    setWorkouts(workout)
  }

  const insertedInformation = (workout) => {
    const new_workouts= [...workouts, workout]
    setWorkouts(new_workouts)

  }

  const logoutBtn = () => {
    removeToken(['mytoken'])

  }

  return (

    <div className="App">



       <Sidebar/>
      <ExerciseList exercises={exercises}/> 
      {/* <WorkoutList workouts={workouts} editBtn={editBtn}/> */}
      {/* {editWorkout ? <Form workout = {editWorkout} updatedInformation = {updatedInformation} insertedInformation = {insertedInformation}/> : null}  */}
      <div className = "col">
        <button onClick = {logoutBtn} className = "btn btn-primary">Logout</button>
      </div>

      </div>


  ) 
}
const Home = () =>{
  <div>
    <h1>Home Page </h1>
  </div>
}

export default App;
