import React from 'react'
import {useForm} from "react-hook-form"
import APIService from '../APIService'
import Select from 'react-select'
import {useCookies} from 'react-cookie';
import {useState, useEffect} from 'react'

function GenerateForm(props) {

    const [planinfo, setPlaninfo] = useState({number:"",type:"",target:"", exercises:[]})



    const handleChange = e => {
        const {name, value} = e.target;
        setPlaninfo(prevState => ({
            ...prevState,
            [name]:value
        }))
    }

    let all_names = props.exercises && props.exercises.map(function(item){
        return {value:item.exercise_name};
    })

    return (
        <form>
            <label className = "form-label">Number of workouts in plan</label>
            <Select
                name="number" 
                options = {[{value:1},{value:2},{value:3},{value:4}]}
                getOptionLabel={options => options["value"]}
                getOptionValue={(options) => options['value']}  
                type="number_of_workouts"
                onChange={handleChange} 
            />
            <label className = "form-label">Plan Target</label>

            <Select name="target"
                options = {[{value:"Strength building"},{value:"Weight Loss"},{value:"Endurance"}]}
                getOptionLabel={options => options["value"]}
                getOptionValue={(options) => options['value']}  
                type="target"
                onChange={handleChange} 
            />
            <label className = "form-label">Time per workout</label>
            <br/>
            <input type="text"  name="time" placeholder = "Enter Time" onChange={handleChange}/>
            <br/>

            <label className = "form-label">Mandatory exercises</label>
            <Select options={all_names} 
                isMulti
                className="basic-multi-select"
                getOptionLabel={options => options["value"]}
                getOptionValue={(options) => options['value']} 
                name="exercises"
                onChange={handleChange}
            />
        </form>
    )
}

export default GenerateForm
