import React, {useState, useEffect} from 'react'
import APIService from '../APIService'


function Form(props) {
    const [title, SetWorkouttitle] = useState('')

    const UpdateWorkout = () => {
        APIService.UpdateWorkout(props.workout.id, {title})
        .then(resp => props.updatedInformation(resp))


    }

    useEffect(() => {
        SetWorkouttitle(props.workout.title)

    }, [props.workout])

    return (
        <div>
            {props.workout ? (
               <div className = "mb-3">
               <label htmlFor = "title" className = "form-label">{props.workout.name}</label>
               <input type="text" className = "form-control" id="title" placeholder = "Change workout name"
               value = {title} onChange = {e => SetWorkouttitle(e.target.value)}></input>

               <br/>
               {
                    <button onClick = {UpdateWorkout} className = "btn btn-success">Update Workout Name</button>
                }

               </div>            
            ):null}

        </div>
    )
}

export default Form
