import React from 'react'
import PropTypes from 'prop-types'
import APIService from '../APIService';
import {useState, useEffect} from 'react'
import { useParams } from "react-router-dom";
import {useCookies} from 'react-cookie';
import WorkoutList from './WorkoutList';
import ExerciseList from './ExerciseList';
import WorkoutForm from './WorkoutForm';

function TrainingPlan(props) {



    let {slug} = useParams();
    const [token] = useCookies(['mytoken'])
    const [plan, setPlan] = useState("")
    const [workouts, SetWorkouts] = useState("")
    const [clicked, setClicked] = useState(false);


    const buttonHandler = () => {
        setClicked(current => !current)
    }


    useEffect(() => {
        console.log(slug)
        APIService.PlanDetail(slug,token['mytoken'])
        .then(resp => setPlan(resp))
        .then(resp => SetWorkouts(resp[0].workouts))
        .catch(error => console.log(error))

    }, [])

    const dltWorkout = (workout) => {
        const new_workouts = workouts.filter(myworkouts => {
          if(myworkouts.id === workout.id) {
            return false
          }
          return true;
        })
        SetWorkouts(new_workouts)
      }

    const addNewWorkout= (workout) => {
        console.log("test czy doszło", workout)
        SetWorkouts(workouts => [...workouts,workout])
    }


    return (
        <div>
            <br/>
            <button type="button" class="btn btn-success" style = {{height:"60px",width: "300px"}}onClick={buttonHandler} >
                 Create new Workout
            </button>

            <br/>
            <br/>

            {plan && <WorkoutList workouts = {plan[0].workouts} dltWorkout={dltWorkout}/>}
            {clicked ? <WorkoutForm plan_id={plan[0].id} addNew = {addNewWorkout}/>:<div></div>}

        </div>
    )
}

TrainingPlan.propTypes = {

}

export default TrainingPlan
