import React from 'react'
import {useState, useEffect} from 'react'
import APIService from '../APIService'
import {useCookies} from 'react-cookie';
function PlanForm() {

    const [plan,setPlan] = useState({plan_name:"",target:"", starting_point:""})
    const [token] = useCookies(['mytoken'])

    const handleChange = e => {
        const {name, value} = e.target;
        setPlan(prevState => ({
            ...prevState,
            [name]:value
        }))
    }

    const CreatePlan = () => {
        APIService.PlanCreate(plan, token['mytoken'])
        .then(window.location.reload())
        .catch(error => console.log(error))
    }

    return (
        <div className = "mb-3">
        <form className="PlanForm" onSubmit={CreatePlan}>
            <label htmlFor = "plan_name" className = "form-label">Enter new plan name</label>
            <input type="text" className = "form-control" name="plan_name" placeholder = "Please enter workout name"
               onChange = {handleChange}></input>
            <select name="target" onChange = {handleChange}>
                <option value="" selected disabled hidden>Choose here</option>
                <option value="STR">Strength building</option>
                <option value="WL">Weight Loss</option>
                <option value="EN">Endurance</option>
            </select>

            {(plan.target) == "EN" && <label htmlFor = "starting_point" className = "form-label">Enter two kilomiters run time</label>}
            {(plan.target) == "STR" && <label htmlFor = "starting_point" className = "form-label">Enter Powerlifting total</label>}
            {(plan.target) == "WL" && <label htmlFor = "starting_point" className = "form-label">Enter starting weight</label>}
            {plan.target &&  
            <input type="text" className = "form-control" name="starting_point" placeholder = "Please enter value"
               onChange = {handleChange}>   
               </input> }

            <br/>
            <button type="submit">Create</button>
      </form>
      </div>
    )
}

export default PlanForm
