import React from 'react'
import PropTypes from 'prop-types'
import APIService from '../APIService';
import {useState, useEffect} from 'react'
import { useParams } from "react-router-dom";
import {useCookies} from 'react-cookie';
import WorkoutList from './WorkoutList';
import ExerciseList from './ExerciseList';
import WorkoutForm from './WorkoutForm';

function TrainingPlan(props) {



    let {slug} = useParams();
    const [token] = useCookies(['mytoken'])
    const [plan, setPlan] = useState("")
    const [clicked, setClicked] = useState(false);


    const buttonHandler = () => {
        setClicked(current => !current)
    }
    useEffect(() => {
        console.log(slug)
        APIService.PlanDetail(slug,token['mytoken'])
        .then(resp => setPlan(resp))
        .catch(error => console.log(error))

    }, [])

    return (
        <div style={{margin:"auto"}}>

            <button type="button" class="btn btn-success" style = {{height:"60px",width: "300px"}}onClick={buttonHandler} >
                 Create new Workout
            </button>


            <br/>
            <br/>

            {plan && <WorkoutList workouts = {plan[0].workouts}/>}
            {clicked ? <WorkoutForm plan_id={plan[0].id}/>:<div></div>}

        </div>
    )
}

TrainingPlan.propTypes = {

}

export default TrainingPlan
