import React from 'react'
import {useState, useEffect} from 'react'
import APIService from '../APIService'
import {useCookies} from 'react-cookie';
function PlanForm() {

    const [plan,setPlan] = useState({plan_name:"",target:""})
    const [token] = useCookies(['mytoken'])

    const handleChange = e => {
        const {name, value} = e.target;
        setPlan(prevState => ({
            ...prevState,
            [name]:value
        }))
    }

    const CreatePlan = () => {
        APIService.PlanCreate(plan, token['mytoken'])
        .then(window.location.reload())
        .catch(error => console.log(error))
    }

    return (
        <div className = "mb-3">
        <form onSubmit={CreatePlan}>
        <label htmlFor = "plan_name" className = "form-label">Enter new plan name</label>
               <input type="text" className = "form-control" name="plan_name" placeholder = "Please enter workout name"
               onChange = {handleChange}></input>
        <select name="target" onChange = {handleChange} defaultValue = "STR">
            <option value="WL">Weight Loss</option>
            <option value="STR">Strength building</option>
            <option value="EN">Endurance</option>
            <option selected="selected">
3
</option>

        </select>
        <br />
        <button type="submit">Create</button>
      </form>
      </div>
    )
}

export default PlanForm
