import React from 'react'
import {useState, useEffect} from 'react'
import APIService from '../APIService'
import {useCookies} from 'react-cookie';

function WorkoutForm(props) {


    const [title, SetWorkouttitle] = useState('')
    const [token] = useCookies(['mytoken'])
    const [workouts, SetWorkouts] = useState([])

    useEffect(() => {
        APIService.WorkoutList(token['mytoken'])
        .then(resp => SetWorkouts(resp))
        .catch(error => console.log(error))
    }, [])


    const CreateWorkout = () => {
        APIService.PlanAddWorkout(props.plan_id, {"title":title}, token['mytoken'])
        .then(window.location.reload())
        .catch(error => console.log(error))
    }
    console.log(props.plan_id)

    let all_names = workouts.map(function(item){
        return {value:item.title};
    })

    return (
        <div>
            <div className = "Row">
                <div className = "mb-3">
                    <form onSubmit={CreateWorkout}>
                    <input type="text" className = "form-control" id="title" placeholder = "Please enter workout name"
                    value = {title} onChange = {e => SetWorkouttitle(e.target.value)}></input>
                        <button type="submit">Create</button>
                    </form>
                </div>

            </div>
        </div>
    )
}

export default WorkoutForm
