import PropTypes from 'prop-types'
import APIService from '../APIService';
import React, {useState, useEffect} from 'react'
import Form from './Form';
import {useCookies} from 'react-cookie';

function WorkoutList(props) {

    const [exercise, SetExercise] = useState({exercise_name:"", reps:"", sets:""})
    const [token] = useCookies(['mytoken'])

    const [showForm, setShowForm] = useState(false);


    const ExrForm = () => {
        setShowForm(!showForm);
      }

    const UpdateExercise = (workout, name) => {
        exercise.exercise_name = name
        APIService.UpdateExercise(workout.id, exercise, token['mytoken'])
        .catch(error => console.log(error))


      }


    const editBtn = (workout) =>{
        props.editBtn(workout)
    }


    const deleteBtn = (workout) => {
        APIService.DeleteWorkout(workout.id)
        .then(() => props.deleteBtn(workout))
        .catch(error => console.log(error))

    }


    const deleteExcr = (workout, exercise) =>{

        console.log("TEST", exercise)
        APIService.DeleteExercise(workout.id, exercise, token['mytoken'])
        .then(window.location.reload())
        .catch(error => console.log(error))
    }

    const handleChange = e => {
        const {name, value} = e.target;
        SetExercise(prevState => ({
            ...prevState,
            [name]:value
        }))
    }


    return (
        <div className="WorkoutList">
            {props.workouts && props.workouts.map(workout => {
                return (
                    <div key = {workout.id}>
                        <h3>{workout.title}</h3>
                        {workout.exercises.map(exercise =>{
                            return (

                                <div key = {exercise.exercise_name}>
                                    <div className = "row">

                                        <h2>{exercise.exercise_name}</h2>
                                        <h2>{exercise.reps}</h2>
                                        <h2>{exercise.sets}</h2>

                                    <div className = "col-md-1">
                                        <button className = "btn btn-primary" onClick  = {ExrForm}> Edit Exercise</button>
                                    </div>
                                    <div className = "col-md-2">
                                        <button className = "btn btn-danger" onClick  = {() => deleteExcr(workout,exercise)}> Delete exercise</button>
                                    </div>
                                    {showForm &&
                                    <form onSubmit={() => UpdateExercise(workout, exercise.exercise_name)}>
                                        <label>
                                        Reps:
                                        <input type="text"  name="reps"  onChange={handleChange}/>
                                        </label>
                                        <label>
                                        Sets:
                                        <input type="text"  name="sets" onChange={handleChange}/>
                                        </label>
                                        <input type="submit" value="Change" />

                                  </form>
                                    } 
                                    </div>
                                </div>
                            )
                        })

                        }

                        <div className = "row">
                            <div className = "col-md-1">
                                <button className = "btn btn-primary" onClick  = {() => editBtn(workout)}> Add Exercise</button>
                            </div>
                            {/* <div className = "col-md-2">
                                <button className = "btn btn-success" onClick  = {() => editBtn(workout)}> Add new exercise</button>
                            </div> */}
                            <div className = "col-md-2">
                                <button className = "btn btn-danger" onClick  = {() => deleteBtn(workout)}> Delete workout</button>
                            </div>

                        </div>
                    </div>
                    )
                }
            )}

            </div> 
    )
}

WorkoutList.propTypes = {
    // workouts: []
}

export default WorkoutList
