    fetch('http://127.0.0.1:8000/plans-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token['mytoken']}`,

      }
    })
    .then(resp => resp.json())
    .then(resp => setPlans(resp))


    fetch('http://127.0.0.1:8000/exercise-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',

      }
    })
    .then(resp => resp.json())
    .then(resp => setExercises(resp))

  }, [])


  // const updatedInformation = (plan) => {
  //   const new_plan = plans.map(myplan => {
  //     if(myplan.id === plan.id) {
  //       return plan;
  //     }
