  //   setPlans(new_plans)

  // }


  // const editBtn = (plan) => {
  //   setEditPlan(plan)
  // }

  // const deleteBtn = (plan) => {
  //   const new_plans = plans.filter(myplan => {
  //     if(myplan.id === plan.id) {
  //       return false
  //     }
  //     return true;
  //   })

  //   setPlans(new_plans)

  // }

  function handleLogout() {
    removeToken(['mytoken']);
  }


  return (
    <CookiesProvider>
    <BrowserRouter>
    <div className="App">
      <div className="container"></div>
      <Sidebar/>


        <Switch>
          <Route exact path = "/">
            <Login/>
          </Route>
        </Switch>


        <Switch>
           <Route exact path = "/home">
              <div className = "col">
                <h3>Homepage  </h3>
             </div>    
              </Route>
          </Switch>

          <Switch>
           <Route exact path = "/plans" >
              <div className = "col">
                <PlanList plans={plans} />
             </div>    
              </Route>
          </Switch>


          <Switch>
            <Route path = "/plan/:slug">
              <TrainingPlan/>      
            </Route>
          </Switch>


          <Switch>
            <Route exact path = "/exercises">
              <ExerciseList exercises={exercises}/> 
          </Route>
          </Switch>

          <Switch>
          <Route exact path = "/login">
            <Login/>
          </Route>
        </Switch>
        <br/>      
        <button onClick = {handleLogout} className = "btn btn-primary">Logout</button>
        <br/>
        </div>

      </BrowserRouter>
      </CookiesProvider>

  ) 
}


export default App;
