import React from 'react'
import PropTypes from 'prop-types'
import { FlatList, StyleSheet, Text, View } from 'react-native';
import WorkoutList from './WorkoutList'
import {useState, useEffect} from 'react'
import WorkoutForm from './WorkoutForm'
import { useParams } from 'react-router-dom';
import {useHistory} from 'react-router-dom';
import {useCookies} from 'react-cookie';
import PlanForm from './PlanForm';
import APIService from '../APIService';
function PlanList(props) {

    let history = useHistory()
    const [token] = useCookies(['mytoken'])
    const [clicked, setClicked] = useState(false)
    const [plans, setPlans] = useState([])

    useEffect(() => {
        fetch('http://127.0.0.1:8000/plans-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token['mytoken']}`,

      }
    })
    .then(resp => resp.json())
    .then(resp => setPlans(resp))

    }, [])

    const buttonHandler = () => {
        setClicked(current => !current)
    }
    const DeletePlan = (plan) => {
        APIService.PlanDelete(token['mytoken'],plan.id)
        .then(window.location.reload())
        .catch(error => console.log(error))
    }

    return (
        <div>  
            <br/>
            <br/>
            <div className="row">
        {props.plans && props.plans.map(plan => {
            <button type/>

            return(
                <div>

                <button type="button" className="PlanElement" onClick={() => history.push(`/plan/${plan.id}`)}>{plan.plan_name}

                    <div className = "PlanDelete">
                        <button className = "btn btn-danger" onClick={() => DeletePlan(plan)}> Delete Plan</button>

                    </div>
                    </ button>
                </div>
            )

        })}
        </div>

        {clicked ? <PlanForm /> : <button type="button" class="btn btn-success middle" onClick={buttonHandler} >
                 Create new Training Plan
                </button>}
        </div>
    )
}

PlanList.propTypes = {

}

export default PlanList
