import React from 'react'
import {useState, useEffect} from 'react'
import APIService from '../APIService'

function PlanForm() {

    const [plan,setPlan] = useState({plan_name:"",target:""})
    const [token] = useCookies(['mytoken'])

    const handleChange = e => {
        const {name, value} = e.target;
        setPlan(prevState => ({
            ...prevState,
            [name]:value
        }))
    }

    const CreatePlan = () => {
        APIService.CreatePlan(plan, token['mytoken'])
        .then(window.location.reload())
        .catch(error => console.log(error))
    }

    return (
        <div className = "mb-3">
        <form >
        <label htmlFor = "plan_name" className = "form-label">Enter new plan name</label>
               <input type="text" className = "form-control" name="plan_name" placeholder = "Please enter workout name"
               onChange = {handleChange}></input>
        <select name="target" onChange = {handleChange}>
            <option value="Weight_loss">Weight Loss</option>
            <option value="Strength">Strength building</option>
            <option value="Endurance">Endurance</option>
        </select>
        <br />
        <button type="submit">Wy≈õlij</button>
      </form>
      </div>
    )
}

export default PlanForm
