import React from 'react'
import PropTypes from 'prop-types'
import { FlatList, StyleSheet, Text, View } from 'react-native';
import WorkoutList from './WorkoutList'
import {useState, useEffect} from 'react'
import WorkoutForm from './WorkoutForm'
import { useParams } from 'react-router-dom';
import {useHistory} from 'react-router-dom';
import {useCookies} from 'react-cookie';
import PlanForm from './PlanForm';
import APIService from '../APIService';
function PlanList(props) {

    let history = useHistory()
    const [token] = useCookies(['mytoken'])
    const [clicked, setClicked] = useState(false)


    const buttonHandler = () => {
        setClicked(current => !current)
    }
    const DeletePlan = (plan) => {
        APIService.PlanDelete(token['mytoken'],plan.id)
        .then(window.location.reload())
        .catch(error => console.log(error))
    }

    return (
        <div>
            <button type="button" class="btn btn-success" onClick={buttonHandler} >
                 Create new Training Plan
            </button>
            <br/>
            <br/>
        {props.plans && props.plans.map(plan => {
            <button type/>
            return(
            <div className="row">


                <button type="button" class="btn btn-lg" onClick={() => history.push(`/plan/${plan.id}`)}>{plan.plan_name}</button>
                <div className = "col-md-2">
                    <button className = "btn btn-danger" onClick={() => DeletePlan(plan)}> Delete</button>
                </div>

            </div>
            )
        })}
        {clicked ? <PlanForm /> : <div></div>}

        </div>
    )
}

PlanList.propTypes = {

}

export default PlanList
