import React from 'react'
import {useState, useEffect} from 'react'
import ExerciseList from './components/ExerciseList'
import WorkoutList from './components/WorkoutList'
import Form from './components/Form'
import Sidebar from './components/Sidebar'
import {useCookies} from 'react-cookie';
import { useHistory} from 'react-router-dom';
import {BrowserRouter, Switch, Route} from 'react-router-dom'
import Login from './components/Login'


function App() {

  let history = useHistory()

  const [workouts, setWorkouts] = useState([])
  const [editWorkout,setEditWorkout] = useState(null)
  const [exercises, setExercises] = useState([])
  const [token, setToken, removeToken] = useCookies(['mytoken'])

  useEffect(() => {

    fetch('http://127.0.0.1:8000/workout-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setWorkouts(resp))
    // .then(console.log("testowanie",workouts))
    // .then(console.log(workouts))


    fetch('http://127.0.0.1:8000/exercise-list', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
      }
    })
    .then(resp => resp.json())
    .then(resp => setExercises(resp))

  }, [])

  // useEffect(() => {
  //   if(!token['mytoken']) {
  //       history.push('/')
  //       //window.location.href = '/'

  //   }
  // }, [token])

  const editBtn = (workout) => {
    setEditWorkout(workout)
  }

  const updatedInformation = (workout) => {
    const new_workout = workouts.map(myworkout => {
      if(myworkout.id === workout.id) {
        return workout;
      }
      else {
        return myworkout;
      }
    })
    setWorkouts(new_workout)
  }

  const insertedInformation = (workout) => {
    const new_workouts= [...workouts, workout]
    setWorkouts(new_workouts)

  }

  const deleteBtn = (workout) => {
    const new_workouts = workouts.filter(myworkout => {
      if(myworkout.id === workout.id) {
        return false
      }
      return true;
    })

    setWorkouts(new_workouts)

  }

  const logoutBtn = () => {
    removeToken(['mytoken'])

  }

  const WorkoutForm = () => {
    setEditWorkout({title:'',exercises:[]})
  }

  return (

    <BrowserRouter>
    <div className="App">

        <Switch>
          <Route exact path = "/">
            <Login/>
          </Route>
        </Switch>
        <Switch>
           <Route exact path ="/home">
          <div className = "col">
              <button onClick = {WorkoutForm} className = "btn btn-primary">Insert Workout</button>
            </div>
            <WorkoutList workouts={workouts} editBtn={editBtn} deleteBtn={deleteBtn}/>
            {editWorkout ? <Form workout = {editWorkout} updatedInformation = {updatedInformation} insertedInformation = {insertedInformation}/> : null} 
              {/* <Sidebar/> */}
              </Route>
          </Switch>
          <Switch>
            <Route exact path = "/exercises">
              <ExerciseList exercises={exercises}/> 
          </Route>
          </Switch>      

      </div>
      </BrowserRouter>

  ) 
}


export default App;
