// import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      exercises:[],
      activeExercise:{
        id:null,
        title:'',
      },
      editing:false,
    }
    this.fetchExercises = this.fetchExercises.bind(this)
  };
  componentDidMount(){
    this.fetchExercises()
  }
  fetchExercises(){
    console.log("Fetching...")
    fetch("http://127.0.0.1:8000/exercise-list")
    .then(response => response.json())
    .then(data => 
        this.setState({
          exercises:data
        })
      )
  }


  render(){
    var exercises = this.state.exercises
    return (
      <div className="exercise-container">
        <div id="form-wrapper">
          <form id="form">
            <div className="flex-wrapper">
              <div style={{flex:6}}>
                <input className="form-control" id="title"></input>
              </div>
              <div style={{flex:1}}>
                <input id="submit" className="btn btn-warning" type="submit"></input>
              </div>
            </div>
          </form>
        </div>
        <div id="list-wrapper">
          {exercises.map(function(exercise,index){
            return(
              <div key={index} className="task-wrapper flex-wrapper">
                <div style={{flex:7}}>
                <span>{exercise.name}</span>
                </div>
                </div>
            )
          })}
        </div>
      </div>
    )
  }
}

export default App;
