import React, {useState, useEffect} from 'react'
import APIService from '../APIService'


function Form(props) {
    const [workoutitle, SetWorkouttitle] = useState('')

    const updateArticle = () => {
        APIService.UpdateArticle(props.article.id, {title, description}, token['mytoken'])
        .then(resp => props.updatedInformation(resp))


    }

    return (
        <div>
            {props.workout ? (
               <div className = "mb-3">
               <label htmlFor = "title" className = "form-label">{props.workout.name}</label>
               <input type="text" className = "form-control" id="title" placeholder = "Please Enter new name"
               value = {workoutitle} onChange = {e => SetWorkouttitle(e.target.value)}></input>

               <br/>
               {
                    props.workout.id ?   <button onClick = {updateArticle} className = "btn btn-success">Update Article</button>
                    :  <button onClick = {insertArticle} className = "btn btn-success">Insert Article</button>


                }

               </div>            
            ):null}

        </div>
    )
}

export default Form
