import PropTypes from 'prop-types'
import APIService from '../APIService';
import React, {useState, useEffect} from 'react'
import ExerciseForm from './ExerciseForm';
import {useCookies} from 'react-cookie';
import FinishedForm from './FinishedForm';
// import WorkoutForm from './WorkoutForm';


function WorkoutList(props) {

    const [exercise, SetExercise] = useState({exercise_name:"", reps:"", sets:""})
    const [token] = useCookies(['mytoken'])
    const [chosenWorkout,setchosenWorkout] =  useState("")
    const [exerciseEdit] = useState({workout:"",exercise:""})
    const [showAddForm, setShowAddForm] = useState(false)
    const [showEditForm, setShowEditForm] = useState(false);




    const ExrAddForm = (workout) => {
        setShowAddForm(!showAddForm);
        setchosenWorkout(workout.title)
    }


    const ExrEditForm = (workout, exercise) => {
        setShowEditForm(!showEditForm);
        exerciseEdit.workout = workout.title
        exerciseEdit.exercise = exercise.exercise_name

    }

    const UpdateExercise = (workout, name) => {
        exercise.exercise_name = name
        APIService.UpdateExercise(workout.id, exercise, token['mytoken'])
        .then(window.location.reload())
        .catch(error => console.log(error))
      }


    const deleteBtn = (workout) => {
        APIService.DeleteWorkout(workout.id)
        .then(window.location.reload())
        .catch(error => console.log(error))
    }


    const deleteExcr = (workout, exercise) =>{

        console.log("TEST", exercise)
        APIService.DeleteExercise(workout.id, exercise, token['mytoken'])
        .then(window.location.reload())
        .catch(error => console.log(error))
    }

    const handleChange = e => {
        const {name, value} = e.target;
        SetExercise(prevState => ({
            ...prevState,
            [name]:value
        }))
    }


    return (
        <div className="WorkoutList">

            {props.workouts && props.workouts.map(workout => {
                return (
                    <div className="WorkoutElement" key = {workout.id}>
                        <h3>{workout.title}</h3>
                        {workout.exercises.map(exercise =>{
                            return (

                                <div key = {exercise.exercise_name}>
                                    <div className = "row">
                                        <div className = "col">
                                            <h2>{exercise.exercise_name}</h2>
                                            <h2>Reps:{exercise.reps}</h2>
                                            <h2>Sets:{exercise.sets}</h2>
                                        </div>


                                        <button className = "btn btn-primary test" onClick  = {() => ExrEditForm(workout, exercise)}> Edit Exercise</button>

                                        <button className = "btn btn-danger test" onClick  = {() => deleteExcr(workout,exercise)}> Delete exercise</button>

                                    {showEditForm &&
                                     (exercise.exercise_name == exerciseEdit.exercise) &&
                                      (workout.title == exerciseEdit.workout) && 

                                    <form onSubmit={() => UpdateExercise(workout, exercise.exercise_name)}>
                                        <label>
                                        Reps:
                                        <input type="text"  name="reps"  onChange={handleChange}/>
                                        </label>
                                        <label>
                                        Sets:
                                        <input type="text"  name="sets" onChange={handleChange}/>
                                        </label>
                                        <input type="submit" value="Change" />

                                  </form>
                                    } 
                                    </div>
                                </div>
                            )
                        })

                        }
                        {showAddForm && (workout.title == chosenWorkout) &&
                            <ExerciseForm workout={workout}/>
                        }



                        <div className = "row">
                            <div className = "col-md-2">
                                <button className = "btn btn-secondary" onClick = {() => ExrAddForm(workout)} > 
                                                                        Add new exercise</button>
                            </div>
                            <div className = "col-md-2">
                                <button className = "btn btn-danger" onClick  = {() => deleteBtn(workout)}> Delete workout</button>
                            </div>


                        </div>
                    </div>
                    )
                }
            )}

            </div> 
    )
}


export default WorkoutList
