import {useState, useEffect} from 'react'
import Form from './Form'

function PlanList(props) {


    const [editWorkout,setEditWorkout] = useState(null)
    console.log(props.plans)

    const editBtn = (workout) => {
        setEditWorkout(workout)
    }

    const updatedInformation = (workout) => {
        const new_workout = workouts.map(myworkout => {
        if(myworkout.id === workout.id) {
            return workout;
        }
        else {
            return myworkout;
        }
        })
        setWorkouts(new_workout)
    }

    const insertedInformation = (workout) => {
        const new_workouts= [...workouts, workout]
        setWorkouts(new_workouts)

    }

    const deleteBtn = (workout) => {
        const new_workouts = plan.workouts.filter(myworkout => {
        if(myworkout.id === workout.id) {
            return false
        }
        return true;
        })

        setWorkouts(new_workouts)

    }

    const WorkoutForm = () => {
         setEditWorkout({title:'',exercises:[]})
    }

    return (
        <div>
        {props.plans && props.plans.map(plan => {
            return(<div>
                <button onClick = {WorkoutForm} className = "btn btn-primary">Insert Workout</button>
                <WorkoutList workouts={plan.workouts} editBtn={editBtn} deleteBtn={() => deleteBtn(plan)}/>
                {editWorkout ? <Form workout = {editWorkout} updatedInformation = {() => updatedInformation(plan, )} insertedInformation = {insertedInformation}/> : null} 


            </div>
            )
        })} 
        </div>
    )
}

